mapper.insert:
insert into notice( ntc_sort
				  , ntc_title
				  , ntc_content
				  , ntc_date
				  , ntc_check
				  , ntc_delyn
				  )
		    values( #{ntc_sort}
		    	  , #{ntc_title}
		    	  , #{ntc_content}
		    	  , sysdate()
		    	  , #{ntc_check}
		    	  , 'y'
		    	  )

mapper.update:
update notice set ntc_sort    = #{ntc_sort}
				, ntc_title   = #{ntc_title}
				, ntc_content = #{ntc_content}
				, ntc_delyn	  = #{ntc_delyn}
where ntc_num = #{ntc_num}

